from fastapi_users import schemasfrom pydantic import field_validator, Fieldclass EventVisitorRead(schemas.BaseModel):    id: int    event_id: int    user_id: int    user_status: strclass EventVisitorElement(schemas.BaseModel):    event_id: int    user_id: int    user_status: str = Field(default="Пойду")    @field_validator("user_status")    @classmethod    def validate_user_status(cls, value):        if value not in ["Пойду", "Не пойду"]:            raise ValueError("Email is invalid")        return value