from sqlalchemy import select, delete, updatefrom fastapi import Dependsfrom sqlalchemy.ext.asyncio import AsyncSessionfrom ..database import get_async_sessionasync def get_user_by_email(user_email: str, db,                            session: AsyncSession = Depends(                                get_async_session)):    query = select(db).where(db.c.email == user_email)    result = await session.execute(query)    result_f = result.fetchall()    return result_fasync def get_all_roles_from_db(db, session: AsyncSession = Depends(    get_async_session)):    query = select(db)    result = await session.execute(query)    return result.all()async def change_role_of_user(role_id_change: int, email_of_user: str,                              user_id: int, db,                              session: AsyncSession = Depends(                                  get_async_session)):    statement = update(db).values(role_id=role_id_change).where(        db.c.email == email_of_user, db.c.id == user_id)    await session.execute(statement)    await session.commit()