from fastapi import Dependsfrom sqlalchemy import select, insert, deletefrom sqlalchemy.ext.asyncio import AsyncSessionfrom ..database import get_async_sessionfrom .schemas import SiteVisitorElementasync def get_site_visitors_from_db(db, session: AsyncSession = Depends(    get_async_session)):    query = select(db)    result = await session.execute(query)    return result.all()async def get_site_visitor_by_data(site_visitor_el: SiteVisitorElement, db,                                   session: AsyncSession = Depends(                                       get_async_session)):    query = select(db).where(db.c.user_id == site_visitor_el.user_id,                             db.c.site_id == site_visitor_el.site_id)    result = await session.execute(query)    result_f = result.all()    return result_fasync def create_site_visitor(site_visitor_el: SiteVisitorElement, db,                              session: AsyncSession = Depends(                                  get_async_session)):    statement = insert(db).values(user_id=site_visitor_el.user_id,                                  site_id=site_visitor_el.site_id)    await session.execute(statement)    await session.commit()async def delete_site_visitor_from_db(site_visitor_el: SiteVisitorElement, db,                              session: AsyncSession = Depends(                                  get_async_session)):    statement = delete(db).where(db.c.user_id == site_visitor_el.user_id,                                 db.c.site_id == site_visitor_el.site_id)    await session.execute(statement)    await session.commit()