from typing import Listfrom sqlalchemy import selectfrom fastapi import APIRouter, Dependsfrom sqlalchemy.ext.asyncio import AsyncSessionfrom ..models.models import ticketfrom ..database import get_async_sessionfrom .schemas import TicketReadrouter = APIRouter(    prefix="/ticket",    tags=["Ticket"])@router.get("/", response_model=List[TicketRead])async def get_tickets(        session: AsyncSession = Depends(get_async_session)):    try:        query = select(ticket)        result = await session.execute(query)        return result.all()    except Exception:        return {            "status": "error",            "data": None,            "details": None        }# ORM - Object relational model# SQL Injection