from sqlalchemy import select, deletefrom fastapi import Dependsfrom sqlalchemy.ext.asyncio import AsyncSessionfrom ..database import get_async_sessionasync def get_user_by_email(user_email: str, db,                            session: AsyncSession = Depends(get_async_session)):    query = select(db).where(db.c.email == user_email)    result = await session.execute(query)    result_f = result.all()    return result_fasync def get_user_by_id(user_id: int, db,                         session: AsyncSession = Depends(get_async_session)):    query = select(db).where(db.c.id == user_id)    result = await session.execute(query)    result_f = result.all()    return result_fasync def get_all_users_from_db(db, session: AsyncSession = Depends(get_async_session)):    query = select(db)    result = await session.execute(query)    return result.all()async def delete_user_from_db(user_email: str, db,                              session: AsyncSession = Depends(get_async_session)):    statement = delete(db).where(db.c.email == user_email)    await session.execute(statement)    await session.commit()