from typing import Listfrom sqlalchemy import selectfrom fastapi import APIRouter, Dependsfrom sqlalchemy.ext.asyncio import AsyncSessionfrom ..models.models import user_ticketfrom ..database import get_async_sessionfrom .schemas import UserTicketReadrouter = APIRouter(    prefix="/user_ticket",    tags=["User ticket"])@router.get("/", response_model=List[UserTicketRead])async def get_user_tickets(        session: AsyncSession = Depends(get_async_session)):    try:        query = select(user_ticket)        result = await session.execute(query)        return result.all()    except Exception:        return {            "status": "error",            "data": None,            "details": None        }# ORM - Object relational model# SQL Injection