import bcryptfrom ..config import SALTdef hash_password(password):    hashed_password = bcrypt.hashpw(password.encode('utf-8'), SALT)    return hashed_passworddef verify_password(password, hashed_password):    if isinstance(hashed_password, str):        hashed_password = hashed_password.encode('utf-8')    return bcrypt.checkpw(password.encode('utf-8'),                          hashed_password)